import { defineParts, defineRecipe } from "@pandacss/dev";

const parts = defineParts({
    zoomedWrapper: { selector: '[data-part="wrapper-zoomed"]:has(&)' },
    wrapper: { selector: '[data-part="wrapper"]:has(&)' },
    image: { selector: "&" },
    blurredImg: {
        selector: '[data-part="wrapper"]:has(&) [data-part="blurred"]'
    }
});

export { parts as imageParts };

export const image = defineRecipe({
    className: "image",
    jsx: ["Image"],
    staticCss: ["*"],
    base: parts({
        image: {
            "&[data-zoomed]": {
                transition: "transform {durations.normal} {easings.ease-in-out}",
                _hover: {
                    transform: "scale(var(--hover-zoom-scale, 1.1))"
                }
            }
        },
        wrapper: {
            position: "relative",
            rounded: "inherit"
        },
        zoomedWrapper: {
            overflow: "hidden",
            position: "relative"
        },
        blurredImg: {
            filter: "auto",
            blur: "var(--blur-radius, {blurs.lg})",
            position: "absolute",
            inset: "0",
            zIndex: -1,
            translate: "auto",
            scale: "var(--blurred-zoom-scale, 1.05)",
            saturate: "150%",
            opacity: "var(--blurred-opacity, 0.4)",
            y: "1"
        }
    })
});
